              </div>
            </transition>
          </div>
        </div>
      </transition>

      <div @click="openCategories" class="text-white-100">
        <svg class="w-12 h-12 absolute bottom-0 p-3 fill-current transition-250"
             :class="[ categoriesOpen ? 'rotate-540 opacity-0' : '' ]"
             viewBox="0 0 143.01 112.6"><rect width="17.01" height="17.01" rx="8.5"/><rect x="28.77" y="4.46" width="114.24" height="8.09" rx="4.04"/><rect y="47.8" width="17.01" height="17.01" rx="8.5"/><rect x="28.77" y="52.25" width="114.24" height="8.09" rx="4.04"/><rect y="95.59" width="17.01" height="17.01" rx="8.5"/><rect x="28.77" y="100.05" width="114.24" height="8.09" rx="4.04"/></svg>

        <svg style="backdrop-filter: blur(2px)"
             class="w-12 h-12 absolute bottom-0 p-3 bg-white-30 m-2 rounded-xl fill-current transition-250"
             :class="[ categoriesOpen ? 'rotate-540 opacity-100' : 'opacity-0' ]"
             viewBox="0 0 237.35 232.68"><path d="M1.49,224a5.06,5.06,0,0,0,3.59,8.65,5,5,0,0,0,3.58-1.48L231.16,8.67a5.07,5.07,0,0,0,0-7.17h0a5.08,5.08,0,0,0-7.18,0Z"/><path d="M13.33,1.49A5.06,5.06,0,0,0,4.68,5.08,5,5,0,0,0,6.16,8.66l222.53,222.5a5.07,5.07,0,0,0,7.17,0h0a5.06,5.06,0,0,0,0-7.18Z"/></svg>
      </div>
    </div>
  </div>
</template>

<script>
export default {
  transition: 'slide-right-center',

  data() {
    return {
      categoriesOpen: false,
      searchOpen: false,
      shoppingCartOpen: false,

      mainCategoriesAreOpen: true,
      subCategoriesAreOpen: false,
    }
  },

  mounted() {
    this.$root.$on('smartPhoneSearchOpen', () => {
      this.openSearch()
    })

    this.$root.$on('smartPhoneShoppingCartOpen', () => {
      this.openShoppingCart()
    })
  },

  methods: {
    openCategories() {
      if (this.categoriesOpen === false) {
        this.categoriesOpen = true

      } else {
        this.categoriesOpen = false
      }

      this.$root.$emit('toolbarSmartPhoneOpen')
      this.$root.$emit('smartPhoneCategoriesOpen')
    },

    selectMainCategories() {
      this.mainCategoriesAreOpen = true
      this.subCategoriesAreOpen = false
    },

    selectSubCategories() {
      this.mainCategoriesAreOpen = false
      this.subCategoriesAreOpen = true
    },

    openSearch() {
      if (this.searchOpen === false) {
        this.searchOpen = true
      } else {
        this.searchOpen = false
      }
    },

    openShoppingCart() {
      if (this.shoppingCartOpen === false) {
        this.shoppingCartOpen = true
      } else {
        this.shoppingCartOpen = false
      }
    }

  },
}

</script>

